// import * as THREE from 'three';

const CAMERA_SETTINGS = {
  fov : 75,
  near : 0.1,
  far : 1000,
}

const SPHERE_SETTINGS = {
  radius : 10,
  segments : 100,
  color : 0xfafafa,
}

const BACKGROUND_SETTINGS = {
  color : 0xfafafa,
  height : 1000,
  width : 1000,
  segments : 1
}

export {
  CAMERA_SETTINGS,
  SPHERE_SETTINGS,
  BACKGROUND_SETTINGS,
}
